         var dia=parseInt(tmp1[2])+1
         var dia_string = dia.toString();
       

         //var tmp2 = '"'+dia_string+'-'+tmp1[1]+'-'+tmp1[0]+'"';
         var tmp2 = tmp1[0]+'-'+tmp1[1]+'-'+dia_string;
         console.log(tmp2);
         
         $('#sortida').attr('min',$('#arribada').val());
         $('#sortida').val($('#arribada').val());
      }

